dunes-script-module.script:
if (!scope) {
	throw 'No parent scope specified! Please, pass the parrent scope to the init function!';
}

var wrapperObjects = {};

function vmQuery(arg) {
	var object = arg;
	var type = vmQuery.getObjectType(object);

	if (type == 'Properties') {
		typeSelectorCount = 0;
		for (var typeSelector in object) {
			typeSelectorCount++;
		}
		if (typeSelectorCount === 1) {
			var objects = Server.findAllForType(typeSelector);
			var criteria = arg[typeSelector];
			var obj;
			for each (obj in objects) {
				var match = false;
				for (var propertyName in criteria) {
					if (obj[propertyName] == criteria[propertyName]) {
						match = true;
					} else {
						match = false;
						continue;
					}
				}
				if (match) {
					break;
				}
			}

			if (match) {
				type = typeSelector;
				object = obj;
			} else {
				return null;
			}
		}
	}

	/*var wrapperObject = wrapperObjects[type];
	if (typeof wrapperObject == 'function') {
		return wrapperObjects[type](object);
	}*/

	var wrapperObject = [].concat(wrapperObjects[type]);
	var wrapperObjectConstructor = wrapperObject.pop();
	if (typeof wrapperObjectConstructor == 'function') {
		var args = [object];
		for each (var argIdentifier in wrapperObject) {
			args.push(this[argIdentifier]);
		}

		return wrapperObjectConstructor.apply(scope, args);
	}

	function baseWrapperObject(innerObject) {
		var _innerObj = innerObject;

		this.toNativeObject = function() {
			return _innerObj;
		};

		this.vroTags = function(tagName) {
			var jsonTags = [];

			var tags = Server.findTagsForObject(_innerObj);
			var globalTags = Server.findGlobalTagsForObject(_innerObj);
			if (tagName) {
				var value = tags.get(tagName);

				if (value) return value;

				return globalTags.get(tagName);
			} else {
				for each (var k in globalTags.keys) {
					jsonTags[k] = globalTags.get(k);
				}

				for each (var k in tags.keys) {
					jsonTags[k] = tags.get(k);
				}

				return jsonTags;
			}
		}

		this.vroTags.private = function(tagName, tagValue) {
			var jsonTags = [];

			if (tagName && tagValue) {
				Server.tag(_innerObj, tagName, tagValue);
				return;
			}

			var tags = Server.findTagsForObject(_innerObj);
			if (tagName) {
				return tags.get(tagName);
			} else {
				for each (var k in tags.keys) {
					jsonTags[k] = tags.get(k);
				}

				return jsonTags;
			}
		}

		this.vroTags.global = function(tagName, tagValue) {
			var jsonTags = [];

			if (tagName && tagValue) {
				Server.tagGlobally(_innerObj, tagName, tagValue);
				return;
			}

			var tags = Server.findGlobalTagsForObject(_innerObj);
			if (tagName) {
				return tags.get(tagName);
			} else {
				for each (var k in tags.keys) {
					jsonTags[k] = tags.get(k);
				}

				return jsonTags;
			}
		}
	};

	return new baseWrapperObject(object);
}

vmQuery.registerModule = function(type, handler) {
	wrapperObjects[type] = [].concat(handler);
};

vmQuery.getObjectType = function(object) {
	return System.getObjectType(object);
};

scope.$ = vmQuery;

var loadComponents = function() {
	var modules = System.getAllModules();

	for each (var module in modules) {
		if (module.name.indexOf('vmquery') == 0) {
			var actions = module.actions; //TODO: this seems to be not valid attribute name, although, it is documented ???
			if (!actions) {
				actions = module.actionDescriptions; //TODO: fallback, using undocumented attribute name ???
			}
			for each (var action in actions) {
				if (action.name != 'init') {
					module[action.name](scope);
				}
			}
		}
	}
}

loadComponents();
